document.querySelector(".header-nav__btn").addEventListener("click",function(){document.querySelector(".header-nav__menu").classList.toggle("header-nav__menu--active"),document.querySelector(".header-nav__login").classList.toggle("header-nav__login--active")});let menuList=document.querySelectorAll(".header-nav__logo, .header-nav__menu-link, .header-nav__login-link");for(let e=0;e<menuList.length;e++)menuList[e].addEventListener("click",function(){document.querySelector(".header-nav__menu").classList.remove("header-nav__menu--active"),document.querySelector(".header-nav__login").classList.remove("header-nav__login--active")});let closeMenuList=document.querySelectorAll(".header-content, header-bottom, section, footer");for(let e=0;e<closeMenuList.length;e++)closeMenuList[e].addEventListener("click",function(){document.querySelector(".header-nav__menu").classList.remove("header-nav__menu--active"),document.querySelector(".header-nav__login").classList.remove("header-nav__login--active")});const anchors=document.querySelectorAll('a[href*="#"]');for(let t of anchors)t.addEventListener("click",function(e){e.preventDefault();e=t.getAttribute("href").substr(1);document.getElementById(e).scrollIntoView({behavior:"smooth",block:"start"})});